import { GoogleSpreadsheet } from 'google-spreadsheet';
import { JWT } from 'google-auth-library'
import { GenerateCSV } from './csv'

const dotenv = require('dotenv');
dotenv.config()

const SHEET_ID = process.env.GOOGLE_SHEET_ID // spreadsheet key is the long id in the sheets URL

// Initialize auth - see https://theoephraim.github.io/node-google-spreadsheet/#/guides/authentication
const serviceAccountAuth = new JWT({
  // env var values here are copied from service account credentials generated by google
  // see "Authentication" section in docs for more info
  email: process.env.GOOGLE_SERVICE_ACCOUNT_EMAIL,
  key: process.env.GOOGLE_PRIVATE_KEY,
  scopes: [
    'https://www.googleapis.com/auth/spreadsheets',
  ],
});

//
const main = async () => {
  const doc = new GoogleSpreadsheet(SHEET_ID, serviceAccountAuth);

  await doc.loadInfo(); // loads document properties and worksheets
  console.log(doc.title);
  
  const sheet = doc.sheetsByIndex[2]; // select "Weekly data consumed" tab
  const MAX_ROW = 500
  const row_range = 'A1:D' + MAX_ROW

  await sheet.loadCells(row_range); // loads range of cells into local cache - DOES NOT RETURN THE CELLS
  console.log(sheet.cellStats); // total cells, loaded, how many non-empty

  const next_elys_allocation = sheet.getCell(2, 3).value
  let data = []

  let row = 2
  do {
    const pool = sheet.getCell(row,1).value
    const multiplier = sheet.getCell(row,2).value

    if (!pool) {
      break
    }

    data.push({'pool':pool, 'multiplier': multiplier,'eden_next_allocation':'' })
    row ++
  }
  while (row < MAX_ROW); 

  data.push({'pool':'', 'multiplier': '', 'eden_next_allocation': next_elys_allocation})

  GenerateCSV(data)
}

main()